userDefinedLinkHintCss:
div > .vimiumHintMarker {
/* linkhint boxes */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#EB87B6),
  color-stop(100%,#7ac3ff));
border: 1px solid #EB87B6;
}

div > .vimiumHintMarker span {
/* linkhint text */
color: black;
font-weight: bold;
font-size: 12px;
}

div > .vimiumHintMarker > .matchingCharacter {
} 

/* HUD ("heads-up display") bar */
div.vimiumHUD {
  background: #24273A;
  border: 1px solid #EB87B6;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background: #24273A;
}

div.vimiumHUD .hud-find {
  background: #24273A;
  border: none;
  color: #EB87B6;
}

div.vimiumHUD span#hud-find-input{
  color: #EB87B6;  /* 'Foreground' color */
}

div.vimiumHUD span#hud-match-count {
  color: #EB87B6;  /* 'Comment' color */
}

div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: #EB87B6;  /* 'Comment' color */
}
